// src/components/Auth/TwoFactorAuth.tsx
import React, { useState } from 'react';
import { api } from '../../services/api';
import { useLocation } from 'react-router-dom';

const TwoFactorAuth: React.FC = () => {
    const location = useLocation();
    const { username } = location.state || {};  // Retrieve username from the state
    const [otp, setOtp] = useState('');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setOtp(e.target.value);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await api.post('http://localhost:5219/api/Auth/verify-otp', { otp });
            console.log('OTP verification successful:', response.data);
        } catch (error) {
            console.error('OTP verification failed:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Display username in an uneditable input field */}
            <input
                type="text"
                name="username"
                value={username || ''}
                readOnly
                placeholder="Username"
            />
            <input
                type="text"
                name="otp"
                placeholder="Enter OTP"
                value={otp}
                onChange={handleChange}
                required
            />
            <button type="submit">Verify OTP</button>
        </form>
    );
};

export default TwoFactorAuth;
