import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { notification } from 'antd';

const Dashboard: React.FC = () => {
    const navigate = useNavigate();
    const [userData, setUserData] = useState<any>(null);

    useEffect(() => {
        const token = localStorage.getItem('authToken');

        if (!token) {
            // If no token found, redirect to login page
            notification.error({
                message: 'Not Authenticated',
                description: 'You need to log in first.',
            });
            navigate('/login');
        } else {
            // Fetch user data from the backend using the token
            fetchUserData(token);
        }
    }, [navigate]);

    const fetchUserData = async (token: string) => {
        try {
            const response = await fetch('http://localhost:5219/api/user', {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            const data = await response.json();

            if (response.ok) {
                setUserData(data);
            } else {
                notification.error({
                    message: 'Error Fetching User Data',
                    description: data.message || 'Something went wrong.',
                });
            }
        } catch (error) {
            console.error('Error fetching user data:', error);
            notification.error({
                message: 'Error',
                description: 'Failed to fetch user data.',
            });
        }
    };

    const handleLogout = () => {
        // Clear token and navigate to login
        localStorage.removeItem('authToken');
        navigate('/login');
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-[color:var(--lightBackgroundColor)]">
            <h1>Welcome to Your Dashboard</h1>

            {userData ? (
                <div className="user-info">
                    <h2>User Profile</h2>
                    <p><strong>Username:</strong> {userData.username}</p>
                    <p><strong>Email:</strong> {userData.email}</p>
                    <p><strong>Phone:</strong> {userData.phoneNumber}</p>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default Dashboard;
