// src/components/Auth/Login.tsx
import React, { useState, useEffect } from 'react';
import { api } from '../../services/api';
import { getDeviceIdentifier } from '../../utils/deviceIdentifier';
import { useNavigate } from 'react-router-dom';
import { CommonFormTextInput } from '../common/inputs';
import {Link } from 'react-router-dom';
const Login: React.FC = () => {
    const navigate=useNavigate()
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        deviceIdentifier: '',
        lastKnownIP: '',
        userAgent: '',
        browserName: '',
        operatingSystem: '',
    });

    // Fetch device identifier and user agent on component mount
    useEffect(() => {
        const fetchAdditionalData = async () => {
            const identifier = await getDeviceIdentifier();
            const userAgent = navigator.userAgent; // Get browser's user agent
            const lastKnownIP = await fetchPublicIP(); // Get IP Address
            const browserName = getBrowserName(userAgent);
            const operatingSystem = getOSName(userAgent);

            setFormData((prev) => ({
                ...prev,
                deviceIdentifier: identifier,
                userAgent,
                lastKnownIP,
                browserName,
                operatingSystem
            }));
        };

        fetchAdditionalData();
    }, []);

    // Function to fetch user's public IP address
    const fetchPublicIP = async (): Promise<string> => {
        try {
            const response = await fetch('https://api.ipify.org?format=json');
            const data = await response.json();
            return data.ip;
        } catch (error) {
            console.error('Failed to fetch IP address:', error);
            return '';
        }
    };

    const getBrowserName = (userAgent: string): string => {
        if (userAgent.includes('Edg/')) return 'Microsoft Edge';
        if (userAgent.includes('Chrome/')) return 'Google Chrome';
        if (userAgent.includes('Firefox/')) return 'Mozilla Firefox';
        if (userAgent.includes('Safari/') && !userAgent.includes('Chrome/')) return 'Apple Safari';
        return 'Unknown';
    };

    const getOSName = (userAgent: string): string => {
        if (userAgent.includes('Windows')) return 'Windows';
        if (userAgent.includes('Mac')) return 'MacOS';
        if (userAgent.includes('Linux')) return 'Linux';
        if (userAgent.includes('Android')) return 'Android';
        if (userAgent.includes('iPhone') || userAgent.includes('iPad')) return 'iOS';
        return 'Unknown';
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // const handleSubmit = async (e: React.FormEvent) => {
    //     e.preventDefault();
    //     try {
    //         const response = await api.post('http://localhost:5219/api/Auth/login', formData);
    //         if (response.data.requiresOTP) {
    //             // Navigate to OTP verification
    //             console.log('OTP required');
    //         } else {
    //             console.log('Login successful:', response.data);
    //         }
    //     } catch (error) {
    //         console.error('Login failed:', error);
    //     }
    // };
    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await api.post('http://localhost:5219/api/Auth/login', formData);
            if (response.data.needsVerification) {
                // Use navigate for redirect
                navigate('/verify-otp', { state: { username: formData.username } });
            } else {
                console.log('Login successful:', response.data);
            }
        } catch (error) {
            console.error('Login failed:', error);
        }
    };


    return (
        <div className='flex items-center justify-center w-full h-full'>
            <div className='flex items-center justify-center shadow-md p-4 m-4'>
            <form  onSubmit={handleSubmit}>               
                <CommonFormTextInput 
                title='Username'
                value={formData.username}
                onChange={handleChange}
                required={true}
                />
                <CommonFormTextInput 
                title='Password'
                value={formData.password}
                onChange={handleChange}
                required={true}
                />
                <button type="submit">Login</button>
            </form>
        <div className="flex justify-between w-full">
            <div><h3>Forgot Password</h3></div> 
            <div>Create an account?<Link to="/register"> Register</Link></div>
        </div>
        </div>
        </div>
    );
};

export default Login;
