import React, { useState, useEffect } from 'react';
import { api } from '../../services/api';
import { useLocation } from 'react-router-dom';
import { notification, Input, Button } from 'antd';
import { CommonFormTextInput } from '../common/inputs';

const TwoFactorAuth: React.FC = () => {
    const location = useLocation();
    const { username } = location.state || {};  // Retrieve username from the state passed via router
    const [otp, setOtp] = useState('');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setOtp(e.target.value);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        // Ensure username and OTP are both passed in the request
        if (!username || !otp) {
            notification.error({
                message: 'Error',
                description: 'Username and OTP are required.',
            });
            return;
        }

        try {
            // Send both username and OTP in the request body
            console.log(username,"hhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhh")
            const response = await api.post('http://localhost:5219/api/Auth/verify-otp', {
                username,  // Include the username
                otp,       // Include the OTP value
            });
            notification.success({
                message: 'OTP Verified',
                description: 'You have successfully verified your OTP.',
            });
            console.log('OTP verification successful:', response.data);
        } catch (error) {
            notification.error({
                message: 'OTP Verification Failed',
                description: 'Please try again.',
            });
            console.error('OTP verification failed:', error);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-light">
            <div className="flex-col shadow-md p-6 bg-white rounded-md w-full max-w-md">
                <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                    {/* Display username in an uneditable input field */}

                                        <CommonFormTextInput
                        title="Username"
                        value={username}
                        onChange={handleChange}
                        required={true}
                    />
                                        <CommonFormTextInput
                        title="OTP"
                        value={otp}
                        onChange={handleChange}
                        required={true}
                    />

                    <Button type="primary" htmlType="submit" className="w-full">
                        Verify OTP
                    </Button>
                </form>
            </div>
        </div>
    );
};

export default TwoFactorAuth;
