import React, { useState, useEffect } from 'react';
import { notification, Button, Input } from 'antd';
import { useNavigate } from 'react-router-dom';
import { api } from '../../services/api';
import { getDeviceIdentifier } from '../../utils/deviceIdentifier';

const Login: React.FC = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        deviceIdentifier: '',
        lastKnownIP: '',
        userAgent: '',
        browserName: '',
        operatingSystem: '',
    });

    useEffect(() => {
        const fetchAdditionalData = async () => {
            const identifier = await getDeviceIdentifier();
            const userAgent = navigator.userAgent;
            const lastKnownIP = await fetchPublicIP();
            const browserName = getBrowserName(userAgent);
            const operatingSystem = getOSName(userAgent);

            setFormData((prev) => ({
                ...prev,
                deviceIdentifier: identifier,
                userAgent,
                lastKnownIP,
                browserName,
                operatingSystem,
            }));
        };

        fetchAdditionalData();
    }, []);

    const fetchPublicIP = async (): Promise<string> => {
        try {
            const response = await fetch('https://api.ipify.org?format=json');
            const data = await response.json();
            return data.ip;
        } catch (error) {
            console.error('Failed to fetch IP address:', error);
            return '';
        }
    };

    const getBrowserName = (userAgent: string): string => {
        if (userAgent.includes('Edg/')) return 'Microsoft Edge';
        if (userAgent.includes('Chrome/')) return 'Google Chrome';
        if (userAgent.includes('Firefox/')) return 'Mozilla Firefox';
        if (userAgent.includes('Safari/') && !userAgent.includes('Chrome/')) return 'Apple Safari';
        return 'Unknown';
    };

    const getOSName = (userAgent: string): string => {
        if (userAgent.includes('Windows')) return 'Windows';
        if (userAgent.includes('Mac')) return 'MacOS';
        if (userAgent.includes('Linux')) return 'Linux';
        if (userAgent.includes('Android')) return 'Android';
        if (userAgent.includes('iPhone') || userAgent.includes('iPad')) return 'iOS';
        return 'Unknown';
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await api.post('http://localhost:5219/api/Auth/login', formData);
            if (response.data.needsVerification) {
                notification.success({
                    message: 'Login Successful',
                    description: 'Redirecting to OTP verification.',
                });
                navigate('/verify-otp', { state: { username: formData.username } });
            } else {
                notification.success({
                    message: 'Login Successful',
                    description: 'You have been successfully logged in.',
                });
            }
        } catch (error) {
            notification.error({
                message: 'Login Failed',
                description: 'Please check your credentials and try again.',
            });
            console.error('Login failed:', error);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-light">
            <div className="flex-col shadow-md p-6 bg-white rounded-md w-full max-w-md">
                <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                    <Input
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        placeholder="Username"
                        required
                        className="mb-4"
                    />
                    <Input
                        name="password"
                        type="password"
                        value={formData.password}
                        onChange={handleChange}
                        placeholder="Password"
                        required
                        className="mb-4"
                    />
                    <Button type="primary" htmlType="submit" className="w-full">
                        Login
                    </Button>
                    <div className="text-sm text-center">
                        Create an account? <a href="/register" className="font-semibold text-primary hover:text-blue-500">Register</a>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Login;
