import React, { useState, useEffect } from 'react';
import { api } from '../../services/api';
import { getDeviceIdentifier } from '../../utils/deviceIdentifier';
import { useNavigate } from 'react-router-dom';
const Register: React.FC = () => {
    const navigate=useNavigate()
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        phoneNumber: '',
        deviceIdentifier: '',
        lastKnownIP: '',
        userAgent: '',
        browserName: '',
        operatingSystem: '',
    });

    useEffect(() => {
        const fetchAdditionalData = async () => {
            try {
                const identifier = await getDeviceIdentifier();
                const userAgent = navigator.userAgent;
                const lastKnownIP = await fetchPublicIP();
                const browserName = getBrowserName(userAgent);
                const operatingSystem = getOSName(userAgent);

                setFormData((prev) => ({
                    ...prev,
                    deviceIdentifier: identifier,
                    userAgent,
                    lastKnownIP,
                    browserName,
                    operatingSystem,
                }));
            } catch (error) {
                console.error('Failed to fetch device information:', error);
            }
        };

        fetchAdditionalData();
    }, []);

    const fetchPublicIP = async (): Promise<string> => {
        try {
            const response = await fetch('https://api.ipify.org?format=json');
            const data = await response.json();
            return data.ip;
        } catch (error) {
            console.error('Failed to fetch IP address:', error);
            return '';
        }
    };

    const getBrowserName = (userAgent: string): string => {
        if (userAgent.includes('Edg/')) return 'Microsoft Edge';
        if (userAgent.includes('Chrome/')) return 'Google Chrome';
        if (userAgent.includes('Firefox/')) return 'Mozilla Firefox';
        if (userAgent.includes('Safari/') && !userAgent.includes('Chrome/')) return 'Apple Safari';
        return 'Unknown';
    };

    const getOSName = (userAgent: string): string => {
        if (userAgent.includes('Windows')) return 'Windows';
        if (userAgent.includes('Mac')) return 'MacOS';
        if (userAgent.includes('Linux')) return 'Linux';
        if (userAgent.includes('Android')) return 'Android';
        if (userAgent.includes('iPhone') || userAgent.includes('iPad')) return 'iOS';
        return 'Unknown';
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await api.post('http://localhost:5219/api/Auth/register', formData);
            console.log('Registration successful:', response.data);
            if (response.data.needsVerification) {
                // Use navigate for redirect
                navigate('/verify-otp', { state: { username: formData.username } });
            } else {
                console.log('Login successful:', response.data);
            }
        } catch (error) {
            console.error('Registration failed:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
                required
            />
            <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
            />
            <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="phoneNumber"
                placeholder="Phone Number"
                value={formData.phoneNumber}
                onChange={handleChange}
                required
            />
            <button type="submit">Register</button>
        </form>
    );
};

export default Register;
