// src/components/Auth/Login.tsx
import React, { useState, useEffect } from 'react';
import { api } from '../../services/api';
import { getDeviceIdentifier } from '../../utils/deviceIdentifier';

const Login: React.FC = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const [deviceIdentifier, setDeviceIdentifier] = useState<string>('');

    useEffect(() => {
        const fetchDeviceIdentifier = async () => {
            const identifier = await getDeviceIdentifier();
            setDeviceIdentifier(identifier);
        };
        fetchDeviceIdentifier();
    }, []);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await api.post('/login', {
                ...formData,
                deviceIdentifier,
            });
            if (response.data.requiresOTP) {
                // Navigate to OTP verification
                console.log('OTP required');
            } else {
                console.log('Login successful:', response.data);
            }
        } catch (error) {
            console.error('Login failed:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
            />
            <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
            />
            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
