import React, { useState, useEffect } from 'react';
import { api } from '../../services/api';
import { getDeviceIdentifier } from '../../utils/deviceIdentifier';

const Register: React.FC = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        phoneNumber: '',
        deviceIdentifier: '',
        lastKnownIP: '',
        userAgent: '',
        browserName: '',
    });

    useEffect(() => {
        const fetchAdditionalData = async () => {
            const identifier = await getDeviceIdentifier();
            const userAgent = navigator.userAgent; // Get browser's user agent
            const lastKnownIP = await fetchPublicIP(); // Get IP address
            const browserName = getBrowserName(userAgent); // Determine browser name

            setFormData((prev) => ({
                ...prev,
                deviceIdentifier: identifier,
                userAgent,
                lastKnownIP,
                browserName,
            }));
        };

        fetchAdditionalData();
    }, []);

    const fetchPublicIP = async (): Promise<string> => {
        try {
            const response = await fetch('https://api.ipify.org?format=json');
            const data = await response.json();
            return data.ip;
        } catch (error) {
            console.error('Failed to fetch IP address:', error);
            return '';
        }
    };

    const getBrowserName = (userAgent: string): string => {
        if (userAgent.includes('Edg/')) {
            return 'Microsoft Edge';
        } else if (userAgent.includes('Chrome/')) {
            return 'Google Chrome';
        } else if (userAgent.includes('Firefox/')) {
            return 'Mozilla Firefox';
        } else if (userAgent.includes('Safari/') && !userAgent.includes('Chrome/')) {
            return 'Apple Safari';
        } else {
            return 'Unknown';
        }
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await api.post('/register', formData);
            console.log('Registration successful:', response.data);
        } catch (error) {
            console.error('Registration failed:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
                required
            />
            <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
            />
            <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="phoneNumber"
                placeholder="Phone Number"
                value={formData.phoneNumber}
                onChange={handleChange}
                required
            />
            <button type="submit">Register</button>
        </form>
    );
};

export default Register;
