import axios, { AxiosRequestConfig } from 'axios';

// Define the input type for the standardFetch function
interface StandardFetchInput {
  url: string;
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
  body?: any;
  contentType: string;
  controller?: AbortController | null;
}

// Define the response type for the standardFetch function
interface StandardFetchResponse {
  status: string;
  data?: any;
  message?: string;
}

// Derive CommonProfileProps from your backend User schema
interface CommonProfileProps {
  userId: number;
  username: string;
  passwordHash: string;
  phoneNumber: string;
  email: string;
  deviceIdentifier: string;
  lastKnownIP: string;
  userAgent: string;
  operatingSystem: string;
  browserName: string;
  trustedDevice: boolean;
  lastLoginDate: Date;
}

// Replace fetch with axios
export async function standardFetch<T>(input: StandardFetchInput): Promise<T> {
  const { url, method = 'GET', body, contentType, controller } = input;

  const headers: Record<string, string> = {
    'Content-Type': contentType,
  };

  const token = localStorage.getItem('token');
  if (token) {
    headers.Authorization = `Bearer ${token}`;
  } else {
    console.warn('No token found, user may not be authenticated');
  }

  const config: AxiosRequestConfig = {
    url,
    method,
    headers,
    data: body,
    signal: controller?.signal,
  };

  try {
    const response = await axios(config);
    return response.data as T;
  } catch (error: any) {
    if (error.response) {
      console.error('Axios error response:', error.response);
      throw error.response.data; // Return the server response
    } else {
      console.error('Axios error:', error.message);
      throw new Error('An unexpected error occurred.');
    }
  }
}

// Fetch a user profile by ID
type User = CommonProfileProps;
export const fetchUser = async (userId: string): Promise<User> => {
  return standardFetch<User>({
    url: `${process.env.NEXT_PUBLIC_API_URL}/user/${userId}`,
    contentType: 'application/json',
  });
};
